module MyModule\n  def function1\n    puts "function1"\n  end\n\n  def function2\n    puts "function2"\n  end\n\n  def function3\n    puts "function3"\n  end\nend\n\nmodule MyRefinedModule\n  include MyModule\n\n  undef_method :function3\nend\n\nclass MyClass\n  include MyRefinedModule\nend\n\nobj = MyClass.new\nobj.function1  # => "function1"\nobj.function2  # => "function2"\nobj.function3  # => NoMethodError