class Entry < ActiveRecord::Base\n  # ...\n\n  validate :validate_tag_names\n\n  def validate_tag_names\n    @tag_names.split(",").each do |name|\n      tag = Tag.new(name: name.strip)\n      unless tag.valid?\n        errors.add(:tag_names, "Invalid tag name: #{name.strip}")\n      end\n    end\n  end\nend