class MyModel < ApplicationRecord\n  validates_presence_of :field1, :if => :section1?\n  validates_presence_of :field2, :if => :section2?\n  validates_presence_of :field3, :if => :section3?\n\n  private\n\n  def section1?\n    # Return true if the current section is section 1\n    self.section == 'section1'\n  end\n\n  def section2?\n    # Return true if the current section is section 2\n    self.section == 'section2'\n  end\n\n  def section3?\n    # Return true if the current section is section 3\n    self.section == 'section3'\n  end\nend