class MyModel < ApplicationRecord\n  SECTION_VALIDATIONS = {\n    'section1' => [:field1],\n    'section2' => [:field2],\n    'section3' => [:field3]\n  }\n\n  def validate_section\n    current_section = self.section\n    validations = SECTION_VALIDATIONS[current_section]\n    validations.each do |field|\n      errors.add(field, "cannot be blank") if self[field].blank?\n    end\n  end\nend