require 'curses'\n\n# Initialize curses\nCurses.init_screen\nCurses.curs_set(0)  # Hide the cursor\n\n# Create the chat log window\nchat_log_window = Curses::Window.new(10, 40, 0, 0)\nchat_log_window.box("|", "-")\nchat_log_window.refresh\n\n# Create the input prompt window\ninput_prompt_window = Curses::Window.new(1, 40, 11, 0)\ninput_prompt_window.addstr("> ")\ninput_prompt_window.refresh\n\n# Main loop\nloop do\n  # Read input from the user\n  input = input_prompt_window.getstr\n\n  # Clear the input prompt window\n  input_prompt_window.clear\n  input_prompt_window.addstr("> ")\n  input_prompt_window.refresh\n\n  # Add the input to the chat log window\n  chat_log_window.addstr(input + "\n")\n  chat_log_window.refresh\n\n  # Scroll the chat log window if necessary\n  if chat_log_window.getmaxy - chat_log_window.cury < 2\n    chat_log_window.scroll(1)\n    chat_log_window.refresh\n  end\nend\n\n# Clean up\nCurses.close_scree