require 'pdf2image'\nrequire 'image_processing/vips'\n\n# PDF to image conversion\npdf_path = 'path/to/your/document.pdf'\nimage_path = 'path/to/output/image.png'\n\n# Convert the first page (you can loop through all pages if needed)\nPdf2Image.convert(pdf_path, output_format: :png, output_file: image_path, page: 1)\n\n# Optional: Further image processing\nprocessed_image_path = 'path/to/output/processed_image.png'\nImageProcessing::Vips.thumbnail(image_path, "1000x1000", sharpen: true).save(processed_image_path)