# app/controllers/email_controller.rb\nclass EmailController < ApplicationController\n  def create\n    @email_validator = EmailValidator.new(params[:email])\n    if @email_validator.valid?\n      # Email is valid, do something with it\n      render json: { success: true, message: "Email is valid" }\n    else\n      render json: { success: false, message: @email_validator.errors.full_messages.join(", ") }\n    end\n  end\nend