class BooksController < ApplicationController\n  def index\n    @books = Book.all\n  end\n\n  def show\n    @book = Book.find(params[:id])\n  end\n\n  def new\n    @book = Book.new\n  end\n\n  def create\n    @book = Book.new(book_params)\n    if @book.save\n      redirect_to @book, notice: 'Book created successfully'\n    else\n      render :new\n    end\n  end\n\n  private\n\n  def book_params\n    params.require(:book).permit(:title, :author)\n  end\nend