class ApplicationController < ActionController::Base\n  before_action :rate_limit\n\n  private\n\n  def rate_limit\n    ip_address = request.remote_ip\n    timestamp = Time.now.to_i\n    key = "rate_limit:#{ip_address}:#{timestamp}"\n\n    # Use Redis to store request metadata\n    redis = Redis.new\n    count = redis.get(key)\n\n    if count && count.to_i >= 100\n      render status: 429, text: 'Too Many Requests'\n    else\n      redis.incr(key)\n      redis.expire(key, 3600) # expire in 1 hour\n    end\n  end\nend