# app/models/user.rb\nclass User < ApplicationRecord\n  has_one :public_profile\n  has_one :private_profile\n\n  accepts_nested_attributes_for :public_profile, :private_profile\nend\n\n# app/models/public_profile.rb\nclass PublicProfile < ApplicationRecord\n  belongs_to :user\n  has_many :emails\n  has_many :phone_numbers\n\n  accepts_nested_attributes_for :emails, :phone_numbers\nend\n\n# app/models/private_profile.rb\nclass PrivateProfile < ApplicationRecord\n  belongs_to :user\n  has_many :addresses\n  has_many :credit_cards\n\n  accepts_nested_attributes_for :addresses, :credit_cards\nend