# Using OpenSSL\nrequire 'openssl'\n\n# Encrypt the file\ncipher = OpenSSL::Cipher.new('aes-256-cbc')\ncipher.encrypt\nFile.open('database.yml', 'wb') do |f|\n  f.write(cipher.update(File.read('database.yml')) + cipher.final)\nend\n\n# Decrypt the file\ncipher = OpenSSL::Cipher.new('aes-256-cbc')\ncipher.decrypt\nFile.open('database.yml', 'wb') do |f|\n  f.write(cipher.update(File.read('database.yml')) + cipher.final)\nend