require 'test/unit'\nrequire 'nokogiri'\n\nclass HtmlTest < Test::Unit::TestCase\n  def test_html_comparison\n    expected_html = '<html><body>Hello World</body></html>'\n    actual_html = '<html> <body>Hello  World</body> </html>'\n\n    expected_doc = Nokogiri::HTML(expected_html).squish\n    actual_doc = Nokogiri::HTML(actual_html).squish\n\n    assert_equal expected_doc, actual_doc\n  end\nend