require 'digest/sha1'\nrequire 'securerandom'\n\n# When creating a new user account\ndef create_user(password)\n  salt = SecureRandom.hex(16) # generate a random salt\n  hashed_password = Digest::SHA1.hexdigest(salt + password)\n  # Store the salt and hashed password in your database\n  # ...\nend\n\n# When verifying a user's password\ndef verify_password(stored_salt, stored_hashed_password, provided_password)\n  new_hash = Digest::SHA1.hexdigest(stored_salt + provided_password)\n  new_hash == stored_hashed_password\nend