require 'axlsx'\n\nAxlsx::Package.new do |p|\n  p.workbook.add_worksheet do |sheet|\n    # Define header row\n    sheet.add_row ['IP', 'Port 22', 'Port 80']\n\n    # Define data validation rules\n    sheet.data_validation('B1', {\n      type: :list,\n      formula1: 'Yes,No',\n      showErrorMessage: true,\n      errorMessage: 'Invalid value'\n    })\n\n    sheet.data_validation('C1', {\n      type: :list,\n      formula1: 'Yes,No',\n      showErrorMessage: true,\n      errorMessage: 'Invalid value'\n    })\n\n    # Add data rows\n    # ...\n  end\nend.serialize('example.xlsx')