require 'webrick'\nrequire 'webrick/https'\nrequire 'soap/rpc/standaloneServer'\n\nclass Server < SOAP::RPC::HTTPServer\n  # ... your code ...\nend\n\nserver = Server.new\n\nwebrick_options = {\n  :Port               => 1234,\n  :BindAddress        => HelperFunctions.local_ip,\n  :SSLCertificate     => OpenSSL::X509::Certificate.new(File.read("path to cert.cert")),\n  :SSLPrivateKey      => OpenSSL::PKey::RSA.new(File.read("path to rsaprivate.key")),\n  :SSLVerifyClient    => OpenSSL::SSL::VERIFY_NONE,\n}\n\nwebrick_server = WEBrick::HTTPServer.new(webrick_options)\nwebrick_server.mount('/soap', SOAP::RPC::WEBrickServlet, server)\n\ntrap(:INT) { webrick_server.shutdown }\n\nwebrick_server.start