require 'mysql2'\n\n# Connect to the database\nclient = Mysql2::Client.new(\n  host: 'localhost',\n  username: 'your_username',\n  password: 'your_password',\n  database: 'your_database'\n)\n\n# Execute a query\nresult = client.query('SHOW CREATE TABLE your_table')\n\n# Get the result as a string\nresult_string = result.to_a[0]['Create Table']\n\n# Analyze the result string and act accordingly\nif result_string =~ /CONSTRAINT `your_key`/\n  client.query('ALTER TABLE your_table DROP FOREIGN KEY your_key')\nend\n\n# Close the connection\nclient.close