require 'socket'\n\n# Create a TCP server\nserver = TCPServer.new('localhost', 12345)\n\n# Accept incoming connections\nloop do\n  client = server.accept\n  puts "Connection from #{client.peeraddr}"\n\n  # Open the binary file in read-only mode\n  file = File.open('path/to/binary/file', 'rb')\n\n  # Send the file to the client\n  while chunk = file.read(4096)\n    client.puts(chunk)\n  end\n\n  file.close\n  client.close\nend