require 'curses'\n\nCurses.init_screen\nCurses.curs_set(0)  # hide the cursor\nCurses.noecho      # don't echo keyboard input\nCurses.cbreak      # don't require enter key to be pressed\n\nwin = Curses::Window.new(0, 0, 0, 0)  # create a window that fills the screen\nwin.keypad(true)                    # enable keypad mode\n\nloop do\n  win.clear\n  win.addstr(0, 0, "Hello, world!")\n  win.addstr(1, 0, "Press 'q' to quit")\n  win.refresh\n\n  c = win.getch\n  break if c == ?q\nend\n\nCurses.close_scree