# worker.rb (consumer)\nrequire 'bunny'\nrequire 'mysql2'\n\n# Set up RabbitMQ connection and MySQL connection\nconn = Bunny.new('amqp://guest:guest@localhost')\nch = conn.create_channel\nmysql_conn = Mysql2::Client.new(host: 'localhost', database: 'mydb', username: 'myuser', password: 'mypassword')\n\n# Consume messages from queue and update database\nqueue = ch.queue('data_readings')\nqueue.subscribe(block: true) do |delivery_info, properties, payload|\n  data_reading = JSON.parse(payload)\n  # Update MySQL database\n  mysql_conn.query("INSERT INTO data_readings (value) VALUES (#{data_reading['value']})")\nend