require 'win32ole'\n\n# Define the source and destination databases\nsrc_dbs = ['SourceDB1', 'SourceDB2', ... 'SourceDB20']\ndst_db = 'DestinationDB'\n\n# Define the tables to copy\ntables = ['Table1', 'Table2', ... 'Table15']\n\n# Create an ADO connection object\nconn = WIN32OLE.new('ADODB.Connection')\nconn.Open('Provider=Microsoft.Jet.OLEDB.4.0;Data Source=' + dst_db)\n\n# Loop over the source databases and tables\nsrc_dbs.each do |src_db|\n  tables.each do |table|\n    sql = "INSERT INTO #{dst_db}.#{table} SELECT * FROM #{src_db}.#{table}"\n    conn.Execute(sql)\n  end\nend\n\n# Close the connection\nconn.Close