require 'gtk2'\n\n# Create a new window\nwindow = Gtk::Window.new\nwindow.title = "Multiple-Select List Box"\n\n# Create a new list store with one column\nlist_store = Gtk::ListStore.new(String)\n\n# Add some data to the list store\nlist_store.append(["Item 1"])\nlist_store.append(["Item 2"])\nlist_store.append(["Item 3"])\nlist_store.append(["Item 4"])\n\n# Create a new tree view\ntree_view = Gtk::TreeView.new\ntree_view.model = list_store\n\n# Create a new tree view column\ncolumn = Gtk::TreeViewColumn.new("Items", Gtk::CellRendererText.new, :text => 0)\ntree_view.append_column(column)\n\n# Set the selection mode to multiple\ntree_view.selection.mode = Gtk::SELECTION_MULTIPLE\n\n# Add the tree view to the window\nwindow.add(tree_view)\n\n# Show the window and all its contents\nwindow.show_all\n\n# Run the main loop\nGtk.mai