# app/helpers/application_helper.rb\nmodule ApplicationHelper\n  def show_feedback_tab?\n    # Define the controllers and actions where you want to show the feedback tab\n    show_in_controllers = ['Controller1', 'Controller2']\n    show_in_actions = ['action1', 'action2']\n\n    if show_in_controllers.include?(params[:controller]) && show_in_actions.include?(params[:action])\n      true\n    else\n      false\n    end\n  end\nend