# routes.rb\nresources :my_models do\n  member do\n    post :revert\n  end\nend\n\n# my_models_controller.rb\nclass MyModelsController < ApplicationController\n  def revert\n    @my_model = MyModel.find(params[:id])\n    @my_model.revert_to_version(params[:version])\n    redirect_to @my_model, notice: "Reverted to version #{params[:version]}"\n  end\nend\n\n# view (e.g. show.html.erb)\n<%= link_to "Revert to previous version", revert_my_model_path(@my_model, version: @my_model.version - 1), method: :post %>