require 'stanford-core-nlp'\n\ntext = "Mr. Jones felt the warm sun on his face as he stepped out onto the balcony of his summer home in Italy. He was happy to be alive."\n\npipeline = StanfordCoreNLP::Pipeline.new\nsentences = pipeline.process(text).sentences\n\nputs sentences # => ["Mr. Jones felt the warm sun on his face as he stepped out onto the balcony of his summer home in Italy.", "He was happy to be alive."]