module Superpower\n  # instance method\n  def turn_invisible\n    # ...\n  end\n\n  # module method\n  def self.turn_into_toad\n    # ...\n  end\n\n  module Fly\n    def flap_wings\n      # ...\n    end\n  end\nend\n\nclass Superman\n  include Superpower\n  include Superpower::Fly # <--- include the nested module\n\n  def run_away\n    flap_wings # <--- now you can call flap_wings\n    turn_invisible # <--- you can also call turn_invisible\n  end\n\n  def see_bad_guys(bad_guy = lex_luthor)\n    Superpower.turn_into_toad(bad_guy) # <--- correct way to call module method\n  end\nend