# views/shared/_index.html.erb\n<table>\n  <thead>\n    <tr>\n      <% columns.each do |column| %>\n        <th><%= column.name %></th>\n      <% end %>\n    </tr>\n  </thead>\n  <tbody>\n    <% records.each do |record| %>\n      <tr>\n        <% columns.each do |column| %>\n          <td><%= record.send(column.name) %></td>\n        <% end %>\n      </tr>\n    <% end %>\n  </tbody>\n</table>