# routes.rb\nresources :my_models do\n  resources :reversions, only: [:create]\nend\n\n# my_models_controller.rb (no changes needed)\n# my_models/reversions_controller.rb\nclass MyModels::ReversionsController < ApplicationController\n  def create\n    @my_model = MyModel.find(params[:my_model_id])\n    @my_model.revert_to_version(params[:version])\n    redirect_to @my_model, notice: "Reverted to version #{params[:version]}"\n  end\nend\n\n# view (e.g. show.html.erb)\n<%= link_to "Revert to previous version", my_model_reversions_path(@my_model, version: @my_model.version - 1), method: :post %>