require 'thread'\n\n# Create a mutex and a condition variable\nmutex = Mutex.new\ncond = ConditionVariable.new\n\n# Thread 1: waiting thread\nThread.new do\n  mutex.synchronize do\n    puts "Thread 1: waiting..."\n    cond.wait(mutex) # wait for signal\n    puts "Thread 1: received signal"\n  end\nend\n\n# Thread 2: notifying thread\nThread.new do\n  mutex.synchronize do\n    puts "Thread 2: notifying..."\n    cond.signal # notify one waiting thread\n    # cond.broadcast # notify all waiting threads\n  end\nend