# SVN bundle\nbundle "SVN" do\n  menu "SVN" do\n    item "Add" do\n      command "svn add #{ENV['TM_FILEPATH']}"\n    end\n    item "Commit" do\n      command "svn commit -m 'commit message' #{ENV['TM_FILEPATH']}"\n    end\n    # ... other SVN commands\n  end\nend\n\n# FTP bundle\nbundle "FTP" do\n  menu "FTP" do\n    item "Upload" do\n      command "ftp -i #{ENV['TM_FTP_SERVER']} << EOF\nput #{ENV['TM_FILEPATH']}\nEOF"\n    end\n    item "Download" do\n      command "ftp -i #{ENV['TM_FTP_SERVER']} << EOF\nget #{ENV['TM_FILEPATH']}\nEOF"\n    end\n  end\nend