def set_status\n  if status.blank?\n    self.status = case [consulted_legal, consulted_marketing]\n                  when [true, true] then "Pending Approval"\n                  when [true, false] then "Awaiting Marketing Approval"\n                  when [false, true] then "Awaiting Legal Approval"\n                  when [false, false] then "Awaiting Marketing & Legal Approval"\n                  end\n  end\n  true # Needs to return true for the update to go through\nend