# features/step_definitions/users_steps.rb\nrequire 'httparty'\nrequire 'nokogiri'\n\nWhen(/^I send a GET request to "([^"]*)"$/) do |path|\n  @response = HTTParty.get("http://localhost:3000#{path}")\nend\n\nThen(/^the response should be a valid XML feed with (\d+) users$/) do |num_users|\n  xml = Nokogiri::XML(@response.body)\n  expect(xml.css('users').size).to eq(num_users.to_i)\nend