require 'openssl'\n\ninteger = 12345\nbinary_string = [integer].pack('N*')\n\n# Create a new encryption cipher\ncipher = OpenSSL::Cipher.new('AES-128-CBC')\ncipher.encrypt\n\n# Encrypt the binary string\nencrypted_string = cipher.update(binary_string) + cipher.final\n\n# Decrypt the string\ndecrypted_string = cipher.decrypt.update(encrypted_string) + cipher.final\n\n# Convert the decrypted string back to an integer\ndecrypted_integer = decrypted_string.unpack('N*').first\n\nputs "Original integer: #{integer}"\nputs "Decrypted integer: #{decrypted_integer}"