# Gemfile\ngem 'hominid'\n\n# config/environments/*.rb\nHominid.api_key = 'YOUR_MAILCHIMP_API_KEY'\n\n# Create a Mailchimp list and add subscribers\nlist_id = 'YOUR_MAILCHIMP_LIST_ID'\nHominid::List.add_subscriber(list_id, 'user@example.com', {\n  'FNAME' => 'John',\n  'LNAME' => 'Doe'\n})\n\n# Send a campaign to the list\ncampaign_id = 'YOUR_MAILCHIMP_CAMPAIGN_ID'\nHominid::Campaign.send(campaign_id)