#!/usr/local/bin/ruby\n\nrequire 'cgi'\nload 'inc_game.cgi'\ncgi = CGI.new\n\ncookie = CGI::Cookie.new("rubyweb", "CustID=123", "Part=ABC");\n\n# Set the HTTP headers, including the Set-Cookie header\ncgi.header("status" => "OK", "cookie" => [cookie])\n\n# Print the response body\nputs "Content-type: text/html\n\n"\n\n# Rest of your code...\ndoIt = cgi['play']\nplay = Game.new\n\n#welcome\nif doIt == ''\n  puts play.displayGreeting\nend\n\n#choose weapon\nplay.playGame\n\nif doIt == 'Play'\n  move = cgi['weapon']\n  human = play.humanMove(move)\n  computer = play.ComputerMove\n  print human\n  print computer\n  result = play.results(human,computer)\n  play.displayResults(result)\nend