# Movie model\nclass Movie < ApplicationRecord\n  has_many :movie_actors\n  has_many :actors, through: :movie_actors\n  has_many :movie_locations\n  has_many :locations, through: :movie_locations\n  # ... other associations ...\nend\n\n# Actor model\nclass Actor < ApplicationRecord\n  has_many :movie_actors\n  has_many :movies, through: :movie_actors\n  # ... other associations ...\nend\n\n# Location model\nclass Location < ApplicationRecord\n  has_many :movie_locations\n  has_many :movies, through: :movie_locations\n  # ... other associations ...\nend\n\n# MovieActor model (association table)\nclass MovieActor < ApplicationRecord\n  belongs_to :movie\n  belongs_to :actor\nend\n\n# MovieLocation model (association table)\nclass MovieLocation < ApplicationRecord\n  belongs_to :movie\n  belongs_to :location\nend