# app/concerns/activatable.rb\nmodule Activatable\n  extend ActiveSupport::Concern\n\n  included do\n    scope :active, -> { where(active: true) }\n  end\n\n  def activate!\n    update(active: true)\n  end\n\n  def deactivate!\n    update(active: false)\n  end\nend\n\n# app/models/user.rb\nclass User < ApplicationRecord\n  include Activatable\nend\n\n# app/controllers/users_controller.rb\nclass UsersController < ApplicationController\n  def index\n    @users = User.active\n  end\nend