# models/twitter.rb\nclass Twitter < ActiveResource::Base\n  self.site = "http://search.twitter.com"\n  self.format = :json\n\n  def self.search(search_term, last_id=0)\n    find(:all, :params => {:q => search_term, :since_id => last_id.to_s})\n  end\nend\n\n# controllers/twitter_controller.rb\nclass TwitterController < ApplicationController\n  def search\n    @results = Twitter.search(params[:search_term], params[:last_id])\n    # perform some logic on @results\n    render :json => @results\n  end\nend