class MyModelsController < ApplicationController\n  def index\n    my_objects = MyModel.all\n    render json: my_objects\n  end\n\n  def create\n    my_object = MyModel.new(my_model_params)\n    if my_object.save\n      render json: my_object, status: :created\n    else\n      render json: my_object.errors, status: :unprocessable_entity\n    end\n  end\n\n  private\n\n  def my_model_params\n    params.require(:my_model).permit(:name, :description)\n  end\nend