# In your auxiliary app's controller\nbefore_filter :authenticate_with_main_app\n\ndef authenticate_with_main_app\n  credentials = ActionController::HttpAuthentication::Basic.encode_credentials('username', 'password')\n  response = Net::HTTP.start('main_app_url') do |http|\n    http.get('/path/to/resource', 'Authorization' => credentials)\n  end\n\n  if response.code == '200'\n    # authentication successful\n  else\n    # authentication failed\n  end\nend