module CharBoolean\n  extend ActiveSupport::Concern\n\n  module ClassMethods\n    def char_boolean_accessor(*fields)\n      fields.each do |field|\n        define_method("#{field}?") do\n          self.send(field) == 'Y'\n        end\n\n        define_method("#{field}=") do |value|\n          self.write_attribute(field, value ? 'Y' : 'N')\n        end\n      end\n    end\n  end\nend