require 'net/http'\n\n# Set your SMS Gateway credentials\nusername = 'your_username'\npassword = 'your_password'\ngateway_url = 'https://your_gateway_url.com/send'\n\n# Set the recipient's phone number and message\nrecipient_phone_number = '+1234567890'\nmessage = 'Hello from Rails!'\n\n# Create the HTTP request\nuri = URI.parse(gateway_url)\nhttp = Net::HTTP.new(uri.host, uri.port)\nhttp.use_ssl = true\nrequest = Net::HTTP::Post.new(uri.request_uri)\nrequest.set_form_data({\n  'username' => username,\n  'password' => password,\n  'recipient' => recipient_phone_number,\n  'message' => message\n})\n\n# Send the request\nresponse = http.request(request)\n\n# Check the response status\nif response.code == '200'\n  puts 'Message sent successfully!'\nelse\n  puts 'Error sending message:', response.code\nend