require 'nokogiri'\n\n# XHTML validation\nxhtml = File.read('example.xhtml')\nxsd = File.read('xhtml1-strict.xsd')\ndoc = Nokogiri::XML(xhtml)\nxsd = Nokogiri::XML::Schema(xsd)\nerrors = xsd.validate(doc)\n\nif errors.empty?\n  puts "XHTML is valid"\nelse\n  puts "XHTML is invalid:"\n  errors.each do |error|\n    puts error.message\n  end\nend\n\n# ATOM validation\natom = File.read('example.atom')\nxsd = File.read('atom.xsd')\ndoc = Nokogiri::XML(atom)\nxsd = Nokogiri::XML::Schema(xsd)\nerrors = xsd.validate(doc)\n\nif errors.empty?\n  puts "ATOM is valid"\nelse\n  puts "ATOM is invalid:"\n  errors.each do |error|\n    puts error.message\n  end\nend\n\nrequire 'css_parser'\n\n# CSS validation\ncss = File.read('example.css')\nparser = CssParser::Parser.new\nparser.load_string!(css)\n\nif parser.errors.empty?\n  puts "CSS is valid"\nelse\n  puts "CSS is invalid:"\n  parser.errors.each do |error|\n    puts error\n  end\nend