# Rails example\ndef download_word_document\n  doc = Docx::Document.new\n  # Add content to the document...\n  doc.save('example.docx')\n  send_file('example.docx', type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document')\nend\n\n# Sinatra example\nget '/download_word_document' do\n  doc = Docx::Document.new\n  # Add content to the document...\n  doc.save('example.docx')\n  send_file 'example.docx', type: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'\nend