require 'docsplit'\nrequire 'RMagick'\n\n# Set the input file\ninput_file = 'path/to/your/document.doc'\n\n# Convert the document to an image\nDocsplit.extract_images(input_file, :output => 'path/to/output/directory')\n\n# Get the first image (thumbnail)\nimage_file = Dir["path/to/output/directory/*.jpg"].first\n\n# Resize the image to a thumbnail\nimg = Magick::Image.read(image_file).first\nimg.resize_to_fit!(100, 100) # resize to 100x100 pixels\nimg.write('thumbnail.jpg')\n\n# Display the thumbnail\nsend_file('thumbnail.jpg', :type => 'image/jpeg', :disposition => 'inline')