<% form_for @post, :url => {:action => :createInnovation } do |form| %>\n  <fieldset>\n    <p> Title: <br/><%= form.text_field :title, :html => {:class => "text ui-widget_content ui-corner-all" }, :value => CGI.escapeHTML(form.object.title) %> </p>\n    <p> Description: <br/> <%= form.text_area :body, :html => {:class => "text ui-widget_content ui-corner-all" }, :value => CGI.escapeHTML(form.object.body) %> </p>\n  </fieldset>\n<%end%>