// in your thumbnail partial\n<div class="thumbnail" data-asset-id="<%= asset.id %>">\n  <%= image_tag(asset.thumbnail_url) %>\n</div>\n\n// in your JavaScript file (e.g. application.js)\n$(document).ready(function() {\n  $('.thumbnail').click(function() {\n    var assetId = $(this).data('assetId');\n    $.ajax({\n      type: 'GET',\n      url: '/assets/' + assetId + '/edit',\n      dataType: 'html',\n      success: function(data) {\n        $('#preview').html(data);\n      }\n    });\n  });\n});