<% with = "'amount='+value+'&item=#{item.id.to_s}&pick_up_time='+document.getElementById('reservation_pick_up_time').value+'&return_time='+document.getElementById('reservation_return_time').value" %>\n\n<%= text_field_tag "reservation[#{prefix}reserved_items][#{item.id}]", get_amount_value(item, @reservation), :size => 3, :onchange => "#{remote_function(:url => { :controller => :items, :action => :availability }, :with => with, :update => availability_url) }" %>