<% for room in @scrape %>\n  <tr id="page_<%= room.id %>">\n    <th scope="row" class="<%= cycle("spec", "specalt") -%>"><%=h room.name %></td>\n    <% Room.columns.each do |column| %>\n      <% if column.name =~ /day\d+/ %>\n        <td class="<%=current_cycle%>"><%=h room.send(column.name) %></td>\n      <% end %>\n    <% end %>\n    <td class="<%=current_cycle%>"><%= select_tag("room[#{room.id}]", options_for_select(0..room.spots,0)) %></td>\n  </tr>\n<% end %>