# In your auxiliary app's controller\nrequire 'rest_client'\n\ndef authenticate_with_main_app\n  url = 'https://main_app_url/path/to/resource'\n  credentials = { username: 'username', password: 'password' }\n  response = RestClient::Request.execute(method: :get, url: url, user: credentials[:username], password: credentials[:password])\n\n  if response.code == 200\n    # authentication successful\n  else\n    # authentication failed\n  end\nend