class QueryForm\n  include ActiveModel::Validations\n\n  attr_accessor :name, :age, :graph_type, :graph_width, :theme\n\n  validates :name, presence: true\n  validates :age, presence: true, numericality: true\n  validates :graph_type, presence: true, inclusion: { in: ['development', 'graph'] }\n  validates :graph_width, presence: true, numericality: true\n  validates :theme, presence: true\nend